export_njk_template:
{% set theId = props.id or props.name %}
{% set labelObj = props.label %}
{% set hintObj = props.hint %}
{% set sizeMb = props.maxSizeMb or 10 %}
{% set types = props.accept or '.pdf,.jpg,.jpeg,.png,.gif,.heic' %}
<div class="govuk-form-group {{ props.formGroup.classes }}">
  {% if labelObj %}
  <label class="govuk-label {{ labelObj.classes }}" for="{{ theId }}">{{ labelObj.text }}</label>
  {% endif %}
  {% if hintObj and hintObj.text %}<div class="govuk-hint">{{ hintObj.text }}</div>{% endif %}
  <input class="govuk-file-upload {{ props.classes }}" id="{{ theId }}" name="{{ props.name }}" type="file"{% if props.multiple %} multiple{% endif %}{% if props.accept %} accept="{{ props.accept }}"{% endif %}{% if props.documentType %} data-document-type="{{ props.documentType }}"{% endif %}{% if props.maxSizeMb is defined %} data-max-size-mb="{{ props.maxSizeMb }}"{% endif %}{% if props.disabled %} disabled{% endif %}>
  {% if props.showMimeList %}<div class="govuk-hint">Allowed types: {{ types }}</div>{% endif %}
  {% if props.showMaxSize %}<div class="govuk-hint">Max size: {{ sizeMb }}MB.</div>{% endif %}
</div>
