export_njk_template:
{% set paddingScale = { 's': { 'y': '0.35rem', 'x': '1.25rem' }, 'm': { 'y': '1.35rem', 'x': '1.85rem' }, 'l': { 'y': '2.35rem', 'x': '2.45rem' }, 'xl': { 'y': '3.35rem', 'x': '3.05rem' } } %}
{% set padKey = props.boxPadding or 'm' %}
{% set pad = paddingScale[padKey] or paddingScale['m'] %}
{% set padY = pad.y %}
{% set padX = pad.x %}
{% set labelText = props.label and (props.label.text or props.label.en or props.label.fr) %}
{% set labelClass = props.label and props.label.classes %}
{% set hintText = props.hint and (props.hint.text or props.hint.en or props.hint.fr) %}
{% set placeholder = props.placeholder and (props.placeholder.text or props.placeholder.en or props.placeholder.fr) %}
{% set actionLabel = props.actionLabel and (props.actionLabel.text or props.actionLabel.en or props.actionLabel.fr) or 'Sign' %}
{% set clearLabel = props.clearLabel and (props.clearLabel.text or props.clearLabel.en or props.clearLabel.fr) or 'Clear' %}
{% set statusSigned = props.statusSignedText and (props.statusSignedText.text or props.statusSignedText.en or props.statusSignedText.fr) or props.statusSignedText or 'Signed' %}
{% set statusUnsigned = props.statusUnsignedText and (props.statusUnsignedText.text or props.statusUnsignedText.en or props.statusUnsignedText.fr) or props.statusUnsignedText or 'Not signed' %}
<div class="app-signature-ack">
  {% if labelText %}<label class="govuk-label{{ labelClass and (' ' + labelClass) or '' }}" for="{{ props.id or props.name }}">{{ labelText }}</label>{% endif %}
  {% if hintText %}<div class="govuk-hint">{{ hintText }}</div>{% endif %}
  <div class="signature-ack__row" style="display:flex;align-items:center;gap:12px;flex-wrap:wrap;">
    <div class="signature-ack__input-wrapper" style="flex:0 0 33%;min-width:260px;max-width:420px;">
      <input class="govuk-input signature-ack__input" id="{{ props.id or props.name }}" name="{{ props.name }}" type="text" value="{{ props.value or '' }}"{% if placeholder %} placeholder="{{ placeholder }}"{% endif %}{% if props.required %} aria-required="true" required{% endif %} style="padding: {{ padY }} {{ padX }}; text-align:center; min-height: calc({{ padY }} * 2 + 1.25rem); width:100%;" data-component="signature-ack" />
    </div>
    <div class="signature-ack__actions" style="display:flex;gap:8px;align-items:center;">
      <button type="button" class="govuk-button">{{ actionLabel }}</button>
      <button type="button" class="govuk-button govuk-button--warning">{{ clearLabel }}</button>
    </div>
    <div class="signature-ack__status govuk-hint" data-signature-status style="flex-basis:100%;margin-top:4px;">{% if props.value %}{{ statusSigned }}{% else %}{{ statusUnsigned }}{% endif %}</div>
  </div>
</div>
