export_njk_template:
{% set cls = props.classes or 'govuk-body' %}
{% if props.html %}
  {% set body = props.html %}
{% else %}
  {% set t = props.text %}
  {% if t is string %}
    {% set body = t %}
  {% else %}
    {% set body = (t and (t.en or t.fr)) or '' %}
  {% endif %}
{% endif %}
{% if cls and (cls.indexOf('govuk-heading-') != -1) %}
<h2 class="{{ cls }}">{{ body | safe }}
</h2>{% elseif cls and (cls.indexOf('govuk-inset-text') != -1) %}
<div class="{{ cls }}">{{ body | safe }}</div>
{% else %}
<p class="{{ cls }}">{{ body | safe }}</p>
{% endif %}
