export_njk_template:
{% from "govuk/components/input/macro.njk" import govukInput %}
{% set cls = props.classes or 'govuk-input' %}
{% if 'govuk-input' not in cls %}{% set cls = 'govuk-input ' + cls %}{% endif %}
{% set isSigned = (value and value.signed) %}
{% set effectiveValue = isSigned and value.name or '' %}
<div class="app-signature-ack">
  {{ govukInput({
    name: props.name,
    id: props.id or props.name,
    label: props.label,
    hint: props.hint,
    classes: cls + (isSigned and ' sig-locked' or ''),
    value: effectiveValue,
    attributes: { 'data-component': 'signature-ack' }
  }) }}
  {# Authoring/export context does not implement interactive Sign/Clear; runtime React renderer handles logic. #}
</div>
